import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import IHabitation from 'src/app/models/habitation.models';
import { HabitationsService } from 'src/app/services/habitations.service';

@Component({
  selector: 'app-lier-habitation',
  templateUrl: './lier-habitation.component.html',
  styleUrls: ['./lier-habitation.component.scss']
})
export class LierHabitationComponent implements OnInit {
  habitations_list:IHabitation[] = [];
  current_habitation_id:number = 0;
  constructor(private service:HabitationsService,private _activeRoute:ActivatedRoute) { }

  async ngOnInit(){
  await this.service.getAllHabitations().subscribe(
    data=>{
      this.habitations_list = data;
      console.log("Chargement des habitations");
    }
  );
  this.current_habitation_id =await Number( this._activeRoute.snapshot.paramMap.get('id'));
  }

}
