import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs/internal/Observable';
import { of } from 'rxjs/internal/observable/of';
import PageTypeQuestion from 'src/app/models/modelsPages/pageTypeQuestions.models';
import ITypequestion from 'src/app/models/typequestion.models';
import { TypeQuestionsService } from 'src/app/services/type-questions.service';

@Component({
  selector: 'app-types-questions',
  templateUrl: './types-questions.component.html',
  styleUrls: ['./types-questions.component.scss']
})
export class TypesQuestionsComponent implements OnInit {

  constructor(private service : TypeQuestionsService) { }

  currentPage:number = 0;
  pageSize:number = 6;
  totalPages:number = 0;
  selected:number = 10;
  getPage(page:number,size:number):Observable<PageTypeQuestion>{

    let index = page * size;
    let totalPages = ~~(this.typeqs_list.length/size);
    if(this.typeqs_list.length % size != 0)
      totalPages ++;
    let pageEquipements = this.typeqs_list.slice(index,index+size);
    return of({
      page:page,
      size:size,
      totalPages:totalPages,
      questions:pageEquipements
    })
  }

  onGetPageTypeQuestions(): void {
    this.getPage(this.currentPage,this.pageSize)
    .subscribe(
      (data)=> {
        this.typeqs_list = data.questions;
        this.totalPages = data.totalPages;


      }
    )
  }


  onSelected(value:string): void {
		this.selected = Number(value);
    if(this.selected != -1 || this.selected < this.typeqs_list.length){
      this.pageSize = this.selected;
    }else{
      this.pageSize =this.typeqs_list.length;
    }
    this.getListTypeQuestions();
	}

  async getListTypeQuestions(){
    await this.service.getAllQuestionsType().subscribe(data=>{
      /*Et on les stocke dans notre variable (habitations_list)*/
      this.typeqs_list = data;
      this.onGetPageTypeQuestions();
    })
  }

  gotoPage(i:number){
    this.currentPage = i;
    this.getListTypeQuestions();

  }
  //Renvoie a l'intitule entre par l'utilisateur lors de la modification d'un type de question
  update_intitule?:string;
  update_label?:string;
  /*renvoie ici au type de question selectionne */ 
  current_typeq?:ITypequestion;
  //Renvoie au la liste des types de question recuperes
  typeqs_list:ITypequestion[] = [];
  //stocke l'intitule du type de question que veux creer/ajouter l'admin 
  intitule_new_typeq:string="";
    //stocke le label du type de question que veux creer/ajouter l'admin 
  label_new_typeq:string="";
  /*(search) contient l'element texte temporaiement saisi dans la barre de recherche 
  qu'on utilise ainsi pour filtrer notre tableau de type de question */
  search:string = "";

  async ngOnInit(){
    await this.service.getAllQuestionsType().subscribe(
      data=>{
        this.typeqs_list = data;
        this.onGetPageTypeQuestions();
      }
    )
  }

  setCurrentTypeQ(typeq:ITypequestion){
    this.update_intitule = typeq.intitule;
    this.update_label = typeq.label;
    this.current_typeq = typeq;
  }

  deleteTypeQuestion(id:any){
    this.service.deleteQuestionType(Number(id)).subscribe(data=>
      {
      console.log(data);
      this.ngOnInit();
      }
      )
  }
  addTypeQuestion(){
    const typeq :ITypequestion = {
      intitule:String(this.intitule_new_typeq),
      label: String(this.label_new_typeq),
      created_at: new Date(),
      updated_at: new Date()
    }
    this.service.postQuestionType(typeq).subscribe(data=>
      {
     this.intitule_new_typeq = "";
     this.label_new_typeq = "";
    console.log(data);  
    this.ngOnInit();
      }
      )}

  updateTypeQuestion(){
    const updateTypeq :ITypequestion = {
      id:Number(this.current_typeq?.id),
      intitule:this.update_intitule!,
      label:this.update_label!,
      updated_at:new Date()  
    }
    this.service.updateQuestionType(updateTypeq).subscribe(
      data =>{
        console.log(data);
        console.log(updateTypeq);
        this.ngOnInit();
      }
    )
  }    


  }
<div *ngIf="typeqs_list.length <1" href="#"
    class=" position-absolute top-50 start-50 translate-middle btn btn-success btn-rounded">
    <i class="fa fa-spinner fa-spin"></i>
</div>
<section class="content">

<div class="panel-body">
    <div class="col-lg-12">
        <div class="view-header">
            <div class="view-header">
                <div class="pull-right text-right" style="line-height: 14px">
                    <div class="header-icon mx-2">
                        <span class="pe-7s-plus" data-toggle="modal" data-target="#addTypeQuestionModal" title="Ajouter nouveau type de question"></span>
                    </div>
                </div>
                <div class="header-icon">
                    <i class="pe page-header-icon pe-7s-note2"></i>
                </div>
                <div class="header-title">
                    <h3> Types Questions</h3>
                    <small>
                        Liste des differentes types de questions
                    </small>
                </div>
            </div>
        </div>
        <hr>
    </div>
    <div class="col-md-12 container-fluid">
        <div class="panel panel-filled">

            <div class="panel-body">

                <div id="tableExample2_wrapper" class="dataTables_wrapper container-fluid dt-bootstrap4 no-footer">
                    <div class="row">
                        <div class="col-sm-6">
                            <div class="dataTables_length" id="tableExample2_length">
                                <label>Show <select #numbers (change)="onSelected(numbers.value)" name="tableExample2_length" aria-controls="tableExample2"
                                        class="form-control form-control-sm">
                                        <option value="6">6</option>
                                        <option value="25">25</option>
                                        <option value="50">50</option>
                                        <option value="-1">All</option>
                                    </select> entries</label>
                            </div>
                        </div>
                        <div class="col-sm-6">
                            <div id="tableExample2_filter" class="dataTables_filter" style="float: right;">
                                <label>Search:<input [(ngModel)]="search" type="text"
                                    title="Entrez l'intitule du type de question a rechercher"
                                        class="form-control form-control-sm" placeholder=""
                                        aria-controls="tableExample2">
                                </label>
                            </div>
                        </div>
                    </div>
                    <table *ngIf="typeqs_list.length >1"
                       id="dtBasicExample" class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
                        <thead>
                            <tr role="row">
                                <th class="th-sm">
                                    Intitule</th>
                                    <th class="th-sm">
                                        Label</th>
                                <th class="th-sm" >
                                    Date creation</th>
                                <th class="th-sm">
                                    Date modification</th>
                                <th class="th-sm" >
                                    Actions</th>

                        </thead>
                        <tbody>
                            <tr *ngFor="let typeqs of typeqs_list">
                                <td  *ngIf="typeqs.intitule.toLowerCase().includes(search) || search==''">{{typeqs.intitule}}</td>
                                <td  *ngIf="typeqs.intitule.toLowerCase().includes(search) || search==''">{{typeqs.label}}</td>
                                <td  *ngIf="typeqs.intitule.toLowerCase().includes(search) || search==''">{{typeqs.created_at | date:'dd/MM/yyyy'}}</td>
                                <td  *ngIf="typeqs.intitule.toLowerCase().includes(search) || search==''">{{typeqs.updated_at | date:'dd/MM/yyyy'}}</td>
                                <td  *ngIf="typeqs.intitule.toLowerCase().includes(search) || search==''" class="d-flex justify-content-between">

                                    <span class="pe-7s-pen" style=" font-size:25px" 

                                    data-toggle="modal" data-target="#myModalUpdate"
                                    title="Modifier Type question"
                                    (click)="setCurrentTypeQ(typeqs)" 
                                    data-toggle="modal" data-target="#updateTypeQuestionModal"></span>

                                    <span class="pe-7s-trash" style=" font-size:25px" 
                                    data-toggle="modal" data-target="#removeTypeQuestionModal"
                                    title="supprimer Type Question" (click)="setCurrentTypeQ(typeqs)"></span>


                            </tr>
                        </tbody>
                    </table>
                    <div class="row">
                        <div class="col-sm-6">
                            <div class="dataTables_info" id="tableExample2_info" role="status" aria-live="polite">
                                Showing 1 to 6 of 25 entries</div>
                        </div>
                        <div class="col-sm-6">
                            <div class="dataTables_paginate paging_simple_numbers" id="tableExample2_paginate">
                                <ul class="pagination">
                                    <li *ngFor="let item of [].constructor(this.totalPages);let i=index;" class="paginate_button page-item ">
                                        <a (click)="gotoPage(i)"  aria-controls="tableExample3" data-dt-idx="3" tabindex="0" class="page-link">{{i}}</a>
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    </div>
    <div class="modal fade" id="addTypeQuestionModal" tabindex="-1" role="dialog" aria-hidden="true">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header text-center">
                    <h4 class="modal-title">Ajouter nouveau Type de Question</h4>
                    <!-- <small>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</small> -->
                </div>
                <div class="panel-body">
                    <div class="form-group">
                        <label for="exampleInputName">Intitule</label>
                        <input type="text" [(ngModel)]="intitule_new_typeq"
                         class="form-control" 
                         placeholder="Intitule habitation">
                         <label for="exampleInputName">Label</label>
                         <input type="text" [(ngModel)]="label_new_typeq"
                         class="form-control" 
                         placeholder="Intitule habitation">
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-default" data-dismiss="modal">Annuler</button>
                    <button type="button" class="btn btn-accent" data-dismiss="modal"
                        (click)="addTypeQuestion()">Nouveau type Question</button>
                </div>
            </div>
        </div>
    </div>
    <!-- Model fot 'Remove habitation' functionality -->
    <div class="modal fade" id="removeTypeQuestionModal" tabindex="-1" role="dialog" aria-hidden="true">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Confirmation suppression </h4>
              </div>
              <div class="modal-body">
                <p>Confirmer la suppression du type de question <em>"{{current_typeq?.intitule}}"</em>.</p>

              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-dismiss="modal"
                (click)="deleteTypeQuestion(current_typeq?.id)">
                Supprimer
                </button>
                <a class="btn btn-w-md btn-success" data-dismiss="modal">Annuler</a>

              </div>
            </div>
          </div>
    </div>
    <!-- Modal for 'Update habitation functionality' -->
    <div class="modal fade" id="updateTypeQuestionModal" tabindex="-1" role="dialog" aria-hidden="true">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Modification de la qestion de type <em>{{current_typeq?.intitule}}</em></h4>
              </div>
              <div class="modal-body">
                <p style="font-weight: bold;">Intitule</p>
                <input type="text" [(ngModel)]="update_intitule" ngDefaultControl
                 class="form-control">
                  <p style="font-weight: bold;">Intitule</p>
                  <input type="text" [(ngModel)]="update_label" ngDefaultControl
                  class="form-control">
             </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-warning" data-dismiss="modal"
                (click)="updateTypeQuestion()">
                Modifier
                </button>
                <a class="btn btn-w-md btn-success" data-dismiss="modal">Annuler</a>

              </div>
            </div>
          </div>
    </div>
    </section>
